package postgresqlconnection;import java.sql.PreparedStatement;import java.sql.SQLException;import java.util.logging.Level;import java.sql.ResultSet;import java.util.logging.Logger;/** * * @author suarez */public class PostgreSqlConnection {    public static void main(String[] args) {        try {            SqlConnection sqlConnection                    = new SqlConnection("grupo23sa", "grup023grup023", "mail.tecnoweb.org.bo",                            "5432", "db_grupo23sa");            String query = "SELECT * FROM \"User\"";            PreparedStatement ps = sqlConnection.connect().prepareStatement(query);            ResultSet rs = ps.executeQuery();            System.out.print("Conexion: " + sqlConnection.connect() + "\n");            System.out.print("Resultado: " + rs.next() + "\n");        } catch (SQLException ex) {            Logger.getLogger(PostgreSqlConnection.class.getName()).log(Level.SEVERE, null, ex);        }    }}